INSERT INTO `config_info`(`id`, `data_id`, `group_id`, `content`, `md5`, `gmt_create`, `gmt_modified`, `src_user`, `src_ip`, `app_name`, `tenant_id`, `c_desc`, `c_use`, `effect`, `type`, `c_schema`) VALUES (16, 'fisher-back-service-dev.yml', 'BACK_GROUP', '#tomacat相关配置\nserver:\n  port: 9002\n  tomcat:\n    max-threads: 500 # Maximum amount of worker threads.\n    min-spare-threads: 50 # Minimum amount of worker threads\n#应用基本配置\nspring:\n  application:\n    name: fisher-back-service\n  boot:\n    admin:\n      client:\n        url: http://localhost:9006\n        instance:\n          service-base-url: http://localhost:9002\n  cloud:\n    nacos:\n      discovery:\n        server-addr: 127.0.0.1:8848\n        metadata:\n          user.name: ${spring.security.user.name}\n          user.password: ${spring.security.user.password}\n  security:\n    user:\n      name: \"fisher\"\n      password: \"fisher\"\n  cache:\n    cache-names: roleResourceCache\n    multi:\n      caffeine:\n        expireAfterAccess: \'10000\'\n      redis:\n        defaultExpiration: \'60000\'\n  #reids配置\n  redis:\n    host: 127.0.0.1\n    port: 6379\n    password:\n  #rabbitmq配置\n  rabbitmq:\n    host: 127.0.0.1\n    port: 5672\n    username: fisher\n    password: fisher\n  #数据源配置\n  datasource:\n    url: jdbc:mysql://localhost:3306/fisher?useUnicode=true&characterEncoding=UTF8&useSSL=false&autoReconnect=true\n    driver-class-name: com.mysql.jdbc.Driver\n    username: root\n    password: 123456\n    hikari:\n      minimum-idle: 0\n#mybatis-plus相关配置\nmybatis-plus:\n  mapper-locations: classpath:/mapper/*Mapper.xml\n  #实体扫描，多个package用逗号或者分号分隔\n  typeAliasesPackage: com.fisher.back.model.entity\n  global-config:\n    db-config:\n      #主键类型  0:\"数据库ID自增\", 1:\"用户输入ID\",2:\"全局唯一ID (数字类型唯一ID)\", 3:\"全局唯一ID UUID\";\n      id-type: AUTO\n      #字段策略 0:\"忽略判断\",1:\"非 NULL 判断\"),2:\"非空判断\"\n      field-strategy: NOT_EMPTY\n      #驼峰下划线转换\n      table-underline: true\n      #mp2.3+ 全局表前缀 mp_\n      #table-prefix: mp_\n      #刷新mapper 调试神器\n      #refresh-mapper: true\n      #数据库大写下划线转换\n      #alipay-mode: true\n      # Sequence序列接口实现类配置\n      #key-generator: com.baomidou.mybatisplus.incrementer.OracleKeyGenerator\n      #逻辑删除配置（下面3个配置）\n      logic-delete-value: 1\n      logic-not-delete-value: 0\n      #sql-injector: com.baomidou.mybatisplus.mapper.LogicSqlInjector\n      #自定义填充策略接口实现\n    #meta-object-handler: com.baomidou.mybatisplus.core.handlers.MetaObjectHandler\n  configuration:\n    #配置返回数据库(column下划线命名&&返回java实体是驼峰命名)，自动匹配无需as（没开启这个，SQL需要写as： select user_id as userId）\n    map-underscore-to-camel-case: true\n    cache-enabled: false\n    #配置JdbcTypeForNull, oracle数据库必须配置\n    jdbc-type-for-null: \'null\'\n# Logger Config\nlogging:\n  level:\n    com.fisher: debug\n    org.springframework: INFO\n#文件上传配置\n#  http:\n#    multipart:\n#      max-file-size: 1000MB\n#      max-request-size: 1000MB\n#feign相关配置\nfeign:\n  hystrix:\n    enabled: true\n  okhttp:\n    enabled: false\n  httpclient:\n    enabled: true\n  client:\n    config:\n      feignName:\n        connectTimeout: 30000\n        readTimeout: 30000\n  compression:\n    request:\n      enabled: true\n    response:\n      enabled: true\n#hystrix配置\nhystrix:\n  threadpool:\n    default:\n      coreSize: 500\n      maxQueueSize: 1000\n      queueSizeRejectionThreshold: 800\n  command:\n    default:\n      execution:\n        isolation:\n          thread:\n            timeoutInMilliseconds: 60000\n#开启端点\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n  endpoint:\n    health:\n      show-details: ALWAYS\n  security:\n    enabled: false', '3be65391449e329ec78e34e72b510dff', '2019-03-08 13:53:02', '2019-03-17 21:50:54', NULL, '127.0.0.1', '', '', '', NULL, NULL, 'yaml', NULL);
