   threadpool:\n#     default:\n#       coreSize: 500\n#       maxQueueSize: 1000\n#       queueSizeRejectionThreshold: 800\n#   command:\n#     default:\n#       execution:\n#         isolation:\n#           thread:\n#             timeoutInMilliseconds: 60000\n\nignore:\n  urls:\n    - /**/*.css\n    - /auth/**\n    - /actuator/**\n    - /admin/user/**\n    - /admin/resource/**\n    - /admin/role/**\n    - /refresh/**\n    - /swagger-ui.html\n    - /swagger-resources/**\n    - /*/v2/api-docs\n    - /webjars/**\n\nswagger:\n  client:\n    - fisher-back-service\n    - fisher-gen-service\n\n# 关闭默认路由\nzuul:\n  ignoredServices: \'*\'\n  sensitive-headers:\n\n#开启端点\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n  endpoint:\n    health:\n      show-details: ALWAYS\nsecurity:\n  enabled: false\n', '80b8ebab25a5202501395babba435569', '2019-03-08 14:08:17', '2019-03-21 18:10:38', NULL, '192.168.30.40', '', '', '', NULL, NULL, 'yaml', NULL);
